import { Component, OnInit } from '@angular/core';
import { filter, Observable, Subscription, tap } from 'rxjs';
import { StateService } from '../services/state.service';

@Component({
  selector: 'app-rolex2',
  templateUrl: './rolex2.component.html',
  styleUrls: ['./rolex2.component.css'],
})
export class Rolex2Component implements OnInit {
  time: number = 0;
  clockSubscription?: Subscription;
  time$: Observable<number> = new Observable();

  constructor(private stateService: StateService) {
    this.time$ = this.stateService.clock$.subscribe()
    console.log(this.stateService);
  }

  ngOnInit(): void {
    this.time$ = this.stateService.clock$.pipe(
      filter((t) => t % 5 === 0),
      tap((t) => {
        console.log(t === 0 ? `kezdés` : `váltás ${t - 5} -> ${t}`);
      })
    );
  }
}
